const campoNombre = document.getElementById('nombre');
const campoEmail = document.getElementById('email');

campoNombre.onkeyup = () => {
    if (campoNombre.value.length < 2) {
        console.log('Nombre no registrado ');
        campoNombre.style.color = 'red';
    } else {
        campoNombre.style.color = 'black';
    }
}

campoEmail.addEventListener('input', () => {
    if ((!campoEmail.value.includes('@')) || (!campoEmail.value.includes('.'))) {
        document.getElementById('mensaje').innerText = "Ingrese un mail ";
    } else {
        document.getElementById('mensaje').innerText = "";
    }
});

function borrarCampos() {
    campoNombre.value = '';
    campoEmail.value = '';
}

const formulario = document.getElementById('formulario');
formulario.addEventListener('submit', validar);

function validar(evento) {
    if ((campoNombre.value == '') || (campoEmail.value == '')) {
        evento.preventDefault();
        alert('Ingrese nombre o email faltante');
    } else {
        const usuario = {
            nombre: campoNombre.value,
            email: campoEmail.value
        };

        const usuarioJSON = JSON.stringify(usuario);

        localStorage.setItem('usuario', usuarioJSON);

        
        fetch('https://jsonplaceholder.typicode.com/posts', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: usuarioJSON
        })
        .then(response => response.json())
        .then(data => {
            console.log('Respuesta de la API:', data);
        })
        .catch(error => {
            console.error('Error al enviar datos del usuario:', error);
        });
    }
}




